export default {
  __name: 'ProductDetail',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const route = useRoute();
    const router = useRouter();
    const cartStore = useCartStore();
    const product = ref({
      id: '',
      name: '',
      price: 0,
      images: [],
      description: ''
    });
    const quantity = ref(1);
    const isAddingToCart = ref(false); // 加载状态

    // 从API获取商品详情
    const fetchProductDetail = async id => {
      if (!id) {
        ElMessage.error('无效的商品ID');
        return;
      }
      try {
        const response = await axios.get(`http://algorineko.top:8080/api/merchant/product/detail/${id}`);
        const apiData = response.data;

        // 转换API数据结构以适应前端需求
        product.value = {
          id: apiData.productId,
          name: apiData.productName,
          price: apiData.price,
          description: apiData.description,
          // 确保images是数组，如果为null则转换为空数组
          images: apiData.images || []
        };
      } catch (error) {
        ElMessage.error('商品加载失败');
        console.error('商品加载错误:', error);
      }
    };
    onMounted(() => {
      const productId = route.params.id;
      if (productId) fetchProductDetail(productId);
    });

    // 从JWT token解析payload
    const parseJwt = token => {
      try {
        const base64Url = token.split('.')[1];
        const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
        const jsonPayload = decodeURIComponent(atob(base64).split('').map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)).join(''));
        return JSON.parse(jsonPayload);
      } catch (e) {
        console.error('Token解析失败:', e);
        return null;
      }
    };
    const addToCart = async () => {
      // 检查用户是否已登录
      const token = localStorage.getItem('token');
      if (!token) {
        ElMessage.warning('请先登录后再添加商品到购物车');
        router.push('/login');
        return;
      }
      isAddingToCart.value = true; // 开始加载

      try {
        // 从token解析用户信息
        const payload = parseJwt(token);
        if (!payload || !payload.customerId) {
          ElMessage.warning('用户信息不完整，请重新登录');
          router.push('/login');
          return;
        }

        // 构造请求数据 - 严格遵循后端API要求
        const requestData = {
          customerId: Number(payload.customerId),
          productId: Number(product.value.id),
          quantity: quantity.value,
          selectedOptions: "" // 空字符串
        };
        console.log("发送到后端的请求数据:", JSON.stringify(requestData, null, 2));

        // 调用后端加入购物车API
        const response = await axios.post('http://algorineko.top:8080/api/customer/cart/add', requestData, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        console.log("后端响应:", response.data);

        // 更新本地购物车状态
        cartStore.addItem({
          id: product.value.id,
          name: product.value.name,
          price: product.value.price,
          quantity: quantity.value,
          image: product.value.images?.[0] || ''
        });
        ElMessage.success('已加入购物车');
      } catch (error) {
        console.error('加入购物车失败:', error);

        // 更详细的错误处理
        if (error.response) {
          console.error('错误详情:', error.response.data);
          if (error.response.status === 400) {
            // 处理400错误
            const errorMsg = error.response.data?.message || '请求参数错误';
            ElMessage.error(`加入购物车失败: ${errorMsg}`);
          } else if (error.response.status === 401) {
            ElMessage.error('身份验证失败，请重新登录');
            router.push('/login');
          } else if (error.response.status === 500) {
            // 显示后端返回的具体错误信息
            const errorMsg = error.response.data?.message || '服务器内部错误';
            ElMessage.error(`加入购物车失败: ${errorMsg}`);
          } else {
            ElMessage.error(`加入购物车失败: ${error.response.data?.message || '未知错误'}`);
          }
        } else {
          // 处理非响应错误（如网络错误）
          ElMessage.error(`加入购物车失败: ${error.message || '网络错误，请检查连接'}`);
        }
      } finally {
        isAddingToCart.value = false; // 结束加载
      }
    };
    const __returned__ = {
      route,
      router,
      cartStore,
      product,
      quantity,
      isAddingToCart,
      fetchProductDetail,
      parseJwt,
      addToCart,
      ref,
      onMounted,
      get useRoute() {
        return useRoute;
      },
      get useRouter() {
        return useRouter;
      },
      get ElMessage() {
        return ElMessage;
      },
      get useCartStore() {
        return useCartStore;
      },
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
