export default {
  __name: 'BusinessView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const router = useRouter();

    // 功能列表
    const functions = ref([{
      key: 'info',
      label: '商家信息',
      icon: 'el-icon-user'
    }, {
      key: 'products',
      label: '商品管理',
      icon: 'el-icon-goods'
    }, {
      key: 'orders',
      label: '订单管理',
      icon: 'el-icon-tickets'
    }, {
      key: 'reviews',
      label: '评价管理',
      icon: 'el-icon-chat-dot-round'
    }]);

    // 当前视图
    const currentView = ref('info');

    // 商家信息 - 初始值从登录信息获取
    const shopInfo = ref({
      id: '',
      name: '',
      address: '',
      contact: '',
      password: '',
      avatar: '',
      credit: 'AAAA级',
      balance: 12580.60
    });

    // 备份初始商家信息用于重置
    const originalShopInfo = ref({});

    // 商品数据（从localStorage加载）
    const products = ref([]);

    // 订单数据
    const orders = ref([{
      orderId: '20231115001',
      createTime: '2023-11-15 09:30:00',
      amount: 256.40,
      status: 'pending'
    }, {
      orderId: '20231115002',
      createTime: '2023-11-15 10:15:00',
      amount: 198.00,
      status: 'completed'
    }, {
      orderId: '20231115003',
      createTime: '2023-11-15 11:00:00',
      amount: 385.90,
      status: 'shipped'
    }]);

    // 评价数据
    const reviews = ref([{
      productName: '有机大米 5kg',
      rating: 4.8,
      content: '米粒饱满，煮饭很香',
      createTime: '2023-11-14 16:30:00',
      replied: false
    }, {
      productName: '特级橄榄油',
      rating: 5.0,
      content: '炒菜味道清香，品质很好',
      createTime: '2023-11-14 17:15:00',
      replied: true
    }]);
    const productFilter = ref({
      keyword: ''
    });
    const productDialogVisible = ref(false);
    const currentProduct = ref({
      id: '',
      categoryId: '',
      name: '',
      price: 0,
      stock: 0,
      description: '',
      images: []
    });
    const isEditing = ref(false);
    const orderFilter = ref({
      status: 'all',
      dateRange: []
    });
    const statusMap = {
      pending: {
        text: '待付款',
        type: 'warning'
      },
      shipped: {
        text: '已发货',
        type: 'primary'
      },
      completed: {
        text: '已完成',
        type: 'success'
      }
    };
    const reviewFilter = ref({
      keyword: '',
      rating: '0'
    });
    const replyDialogVisible = ref(false);
    const replyContent = ref('');
    const currentReview = ref(null);

    // 计算属性 - 过滤商品
    const filteredProducts = computed(() => {
      return (products.value || []).filter(p => (p?.name?.toLowerCase() || '').includes(productFilter.value.keyword?.toLowerCase() || '')).map(p => ({
        ...p,
        price: Number(p.price) || 0,
        stock: Math.max(Number(p.stock) || 0, 0)
      }));
    });
    const filteredOrders = computed(() => {
      return (orders.value || []).filter(o => {
        const statusMatch = orderFilter.value.status === 'all' || o.status === orderFilter.value.status;
        const dateMatch = !orderFilter.value.dateRange?.length || new Date(o.createTime) >= new Date(orderFilter.value.dateRange[0]) && new Date(o.createTime) <= new Date(orderFilter.value.dateRange[1]);
        return statusMatch && dateMatch;
      });
    });
    const filteredReviews = computed(() => {
      return (reviews.value || []).filter(r => {
        const keywordMatch = (r.productName?.toLowerCase() || '').includes(reviewFilter.value.keyword?.toLowerCase() || '');
        const ratingValue = Number(reviewFilter.value.rating);
        const ratingMatch = ratingValue === 0 || ratingValue === 5 && r.rating === 5 || ratingValue === 4 && r.rating >= 4 && r.rating < 5 || ratingValue === 3 && r.rating <= 3;
        return keywordMatch && ratingMatch;
      });
    });
    const dialogTitle = computed(() => isEditing.value ? '编辑商品' : '新增商品');

    // 方法
    const switchView = view => {
      if (['info', 'products', 'orders', 'reviews'].includes(view)) {
        currentView.value = view;
      }
    };
    const logout = () => {
      // 只清除认证信息，保留用户数据
      localStorage.removeItem('token');
      localStorage.removeItem('userId');
      localStorage.removeItem('userName');
      localStorage.removeItem('role');
      router.push('/login');
    };
    const saveShopInfo = () => {
      // 更新本地存储中的商家信息
      const businessUsers = JSON.parse(localStorage.getItem('businessUsers') || '[]');
      const userId = localStorage.getItem('userId');
      if (!userId) {
        ElMessage.error('用户信息异常，请重新登录');
        return;
      }
      const index = businessUsers.findIndex(u => u.userId === userId);
      if (index !== -1) {
        // 更新商家信息
        const updatedUser = {
          ...businessUsers[index],
          userName: shopInfo.value.name,
          address: shopInfo.value.address,
          contact: shopInfo.value.contact,
          avatar: shopInfo.value.avatar
        };

        // 如果输入了新密码，则更新密码
        if (shopInfo.value.password) {
          updatedUser.password = shopInfo.value.password;
        }
        businessUsers[index] = updatedUser;
        localStorage.setItem('businessUsers', JSON.stringify(businessUsers));

        // 更新本地存储中的用户名
        localStorage.setItem('userName', shopInfo.value.name);

        // 更新原始信息备份
        originalShopInfo.value = {
          ...shopInfo.value
        };
        ElNotification.success({
          title: '保存成功',
          message: '商家信息已更新',
          duration: 2000
        });

        // 清空密码字段
        shopInfo.value.password = '';
      } else {
        ElMessage.error('未找到商家信息，请重新登录');
      }
    };
    const resetForm = () => {
      shopInfo.value = {
        ...originalShopInfo.value
      };
      shopInfo.value.password = '';
      ElMessage.info('已重置修改内容');
    };
    const handleAvatarUpload = file => {
      // 在实际应用中，这里应该上传到服务器并获取URL
      // 这里仅模拟上传过程
      return new Promise(resolve => {
        const reader = new FileReader();
        reader.onload = () => {
          shopInfo.value.avatar = reader.result;
          resolve();
        };
        reader.readAsDataURL(file);
      });
    };

    // 处理商品图片上传
    const handleImageUpload = file => {
      return new Promise(resolve => {
        const reader = new FileReader();
        reader.onload = () => {
          currentProduct.value.images.push(reader.result);
          resolve();
        };
        reader.readAsDataURL(file);
      });
    };

    // 移除商品图片
    const removeImage = index => {
      currentProduct.value.images.splice(index, 1);
    };
    const showAddDialog = () => {
      currentProduct.value = {
        id: '',
        categoryId: '',
        name: '',
        price: 0,
        stock: 0,
        description: '',
        images: []
      };
      isEditing.value = false;
      productDialogVisible.value = true;
    };
    const editProduct = product => {
      if (!product?.id) {
        ElMessage.warning('无效的商品数据');
        return;
      }
      currentProduct.value = {
        ...product
      };
      isEditing.value = true;
      productDialogVisible.value = true;
    };

    // 保存商品到localStorage
    const saveProductToLocalStorage = () => {
      const businessId = localStorage.getItem('userId') || 'default';
      const storageKey = `businessProducts_${businessId}`;
      localStorage.setItem(storageKey, JSON.stringify(products.value));
    };

    // 从localStorage加载商品
    const loadProductsFromLocalStorage = () => {
      const businessId = localStorage.getItem('userId') || 'default';
      const storageKey = `businessProducts_${businessId}`;
      const savedProducts = localStorage.getItem(storageKey);
      if (savedProducts) {
        products.value = JSON.parse(savedProducts);
      }
    };
    const saveProduct = () => {
      if (!currentProduct.value.id) {
        ElMessage.error('商品ID不能为空');
        return;
      }
      if (!currentProduct.value.name?.trim()) {
        ElMessage.error('商品名称不能为空');
        return;
      }
      if (currentProduct.value.price < 0) {
        ElMessage.error('价格不能为负数');
        return;
      }
      try {
        if (isEditing.value) {
          const index = products.value.findIndex(p => p?.id === currentProduct.value?.id);
          if (index !== -1) {
            products.value.splice(index, 1, {
              ...currentProduct.value,
              price: Number(currentProduct.value.price) || 0,
              stock: Math.max(Number(currentProduct.value.stock), 0)
            });
          }
        } else {
          // 检查商品ID是否已存在
          if (products.value.some(p => p.id === currentProduct.value.id)) {
            ElMessage.error('商品ID已存在，请使用不同的ID');
            return;
          }
          products.value.push({
            ...currentProduct.value,
            name: currentProduct.value.name || '新商品',
            price: Number(currentProduct.value.price) || 0,
            stock: Math.max(Number(currentProduct.value.stock), 0)
          });
        }

        // 保存到localStorage
        saveProductToLocalStorage();
        productDialogVisible.value = false;
        ElMessage.success('商品保存成功');
      } catch (error) {
        ElMessage.error(`保存失败: ${error.message}`);
      }
    };
    const deleteProduct = id => {
      if (!id) {
        ElMessage.warning('无效的商品ID');
        return;
      }
      const index = products.value.findIndex(p => p?.id === id);
      if (index !== -1) {
        products.value.splice(index, 1);

        // 保存到localStorage
        saveProductToLocalStorage();
        ElMessage.success('商品删除成功');
      }
    };
    const viewOrderDetail = order => {
      if (order?.orderId) {
        console.log('查看订单详情:', order);
      }
    };
    const handleShip = order => {
      if (order?.orderId && order.status === 'pending') {
        order.status = 'shipped';
        ElMessage.success('订单状态已更新');
      }
    };
    const showReplyDialog = review => {
      if (review) {
        currentReview.value = review;
        replyDialogVisible.value = true;
        replyContent.value = '';
      }
    };
    const submitReply = () => {
      if (currentReview.value && replyContent.value.trim()) {
        currentReview.value.replied = true;
        replyDialogVisible.value = false;
        replyContent.value = '';
        ElMessage.success('回复成功');
      }
    };

    // 页面加载时从本地存储获取商家信息和商品数据
    onMounted(() => {
      // 设置商家ID为登录时的用户ID
      const userId = localStorage.getItem('userId');
      const userName = localStorage.getItem('userName');
      shopInfo.value.id = userId || 'BUS2023VIP';
      shopInfo.value.name = userName || '商家名称';

      // 尝试从本地存储获取完整商家信息
      const businessUsers = JSON.parse(localStorage.getItem('businessUsers') || '[]');
      const currentBusiness = businessUsers.find(u => u.userId === userId);
      if (currentBusiness) {
        shopInfo.value = {
          ...shopInfo.value,
          address: currentBusiness.address || '',
          contact: currentBusiness.contact || '',
          avatar: currentBusiness.avatar || ''
        };
      }

      // 保存原始信息用于重置
      originalShopInfo.value = {
        ...shopInfo.value
      };

      // 从localStorage加载商品数据
      loadProductsFromLocalStorage();
    });
    const __returned__ = {
      router,
      functions,
      currentView,
      shopInfo,
      originalShopInfo,
      products,
      orders,
      reviews,
      productFilter,
      productDialogVisible,
      currentProduct,
      isEditing,
      orderFilter,
      statusMap,
      reviewFilter,
      replyDialogVisible,
      replyContent,
      currentReview,
      filteredProducts,
      filteredOrders,
      filteredReviews,
      dialogTitle,
      switchView,
      logout,
      saveShopInfo,
      resetForm,
      handleAvatarUpload,
      handleImageUpload,
      removeImage,
      showAddDialog,
      editProduct,
      saveProductToLocalStorage,
      loadProductsFromLocalStorage,
      saveProduct,
      deleteProduct,
      viewOrderDetail,
      handleShip,
      showReplyDialog,
      submitReply,
      ref,
      computed,
      onMounted,
      get useRouter() {
        return useRouter;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElNotification() {
        return ElNotification;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
