import axios from 'axios'

const apiClient = axios.create({
  baseURL: 'http://algorineko.top:8080/api/user',
  withCredentials: false,
  headers: {
    Accept: 'application/json',
    'Content-Type': 'application/json'
  }
})

export default {
  async login(credentials) {
    try {
      const response = await apiClient.post('/login', {
        username: credentials.userId,
        password: credentials.password
      })
      
      // 从响应中获取用户角色
      const role = response.data.userType;
      
      return {
        data: {
          token: response.data.token || 'simulated-token',
          userId: credentials.userId,
          role: role
        }
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        throw new Error('用户ID或密码错误')
      } else {
        throw new Error('登录失败: ' + (error.message || '网络错误'))
      }
    }
  },

  async register(user) {
    try {
      const response = await apiClient.post('/register', {
        username: user.userId,
        password: user.password,
        phone: user.phone || '',
        userType: user.role,
        address: user.address || ''
      })
      
      return {
        data: {
          message: response.data.message || '注册成功'
        }
      }
    } catch (error) {
      if (error.response && error.response.data) {
        throw new Error(error.response.data.message || '注册失败')
      } else {
        throw new Error('注册失败: ' + (error.message || '网络错误'))
      }
    }
  }
}
