import { ref, onMounted } from 'vue';
import { ElMessage, ElMessageBox } from 'element-plus';
import axios from 'axios';

// 订单状态配置

export default {
  __name: 'UserOrders',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const statusText = {
      PENDING: '待付款',
      PAID: '待发货',
      SHIPPED: '已发货',
      COMPLETED: '已完成',
      CANCELED: '已取消',
      // 添加小写状态映射以兼容原有逻辑
      pending: '待付款',
      paid: '待发货',
      shipped: '已发货',
      completed: '已完成',
      canceled: '已取消'
    };
    const statusType = {
      PENDING: 'warning',
      PAID: 'primary',
      SHIPPED: 'primary',
      COMPLETED: 'success',
      CANCELED: 'info',
      // 添加小写状态映射以兼容原有逻辑
      pending: 'warning',
      paid: 'primary',
      shipped: 'primary',
      completed: 'success',
      canceled: 'info'
    };

    // JWT 解析函数
    const parseJwt = token => {
      try {
        const base64Url = token.split('.')[1];
        const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
        const jsonPayload = decodeURIComponent(atob(base64).split('').map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)).join(''));
        return JSON.parse(jsonPayload);
      } catch (e) {
        console.error('Token解析失败:', e);
        return null;
      }
    };

    // 筛选选项
    const statusOptions = ref([{
      value: 'all',
      label: '全部状态'
    }, {
      value: 'PENDING',
      label: '待付款'
    }, {
      value: 'PAID',
      label: '待发货'
    }, {
      value: 'SHIPPED',
      label: '已发货'
    }, {
      value: 'COMPLETED',
      label: '已完成'
    }]);

    // 订单数据
    const orderLines = ref([]); // 用于存储拆分后的订单行
    const filterStatus = ref('all');
    const filterDate = ref([]);
    const total = ref(0);
    const pageSize = ref(10);
    const currentPage = ref(1);

    // 评价相关
    const reviewDialogVisible = ref(false);
    const reviewForm = ref({
      orderItemId: '',
      // 新增：订单项ID
      customerId: '',
      // 新增：用户ID
      rating: 5,
      content: ''
    });
    const currentReviewOrderLine = ref(null);

    // 物流详情相关
    const trackingDialogVisible = ref(false);
    const currentTrackingInfo = ref({
      logisticsCompany: '',
      trackingNumber: '',
      status: ''
    });

    // 从Token获取用户ID
    const getCustomerIdFromToken = () => {
      const token = localStorage.getItem('token');
      if (!token) {
        ElMessage.error('用户未登录，请先登录');
        return null;
      }
      try {
        // 使用自定义函数解析JWT Token
        const decoded = parseJwt(token);
        // 假设用户ID存储在decoded.id字段中
        return decoded.customerId || null;
      } catch (error) {
        console.error('Token解析失败:', error);
        ElMessage.error('用户信息解析失败');
        return null;
      }
    };

    // 更新订单状态（直接使用大写的英文状态字符串）
    const updateOrderStatus = async (orderId, newStatus) => {
      try {
        const response = await axios.put(`http://algorineko.top:8080/api/order/updateStatus?orderId=${orderId}&status=${newStatus}`, {}, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        return response.data === true;
      } catch (error) {
        console.error('更新订单状态失败:', error);
        ElMessage.error('更新订单状态失败');
        return false;
      }
    };

    // 从后端API加载订单数据
    const loadOrders = async () => {
      try {
        // 从Token获取用户ID
        const customerId = getCustomerIdFromToken();
        if (!customerId) {
          ElMessage.error('无法获取用户信息');
          return;
        }

        // 调用后端API获取订单
        const response = await axios.get(`http://algorineko.top:8080/api/order/customer/${customerId}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        // 转换数据结构
        const backendOrders = response.data.map(order => ({
          orderId: order.orderId,
          merchantId: order.merchantId,
          totalPrice: order.totalPrice,
          status: order.status,
          // 保持大写状态
          createTime: new Date().toLocaleString(),
          // 添加时间字段（后端未提供）
          items: order.items.map(item => ({
            id: item.productId,
            name: `商品 ${item.productId}`,
            // 默认商品名称
            price: item.price,
            quantity: item.quantity,
            image: 'https://via.placeholder.com/60',
            // 默认图片
            orderItemId: item.orderItemId // 新增：保存订单项ID
          }))
        }));

        // 获取所有唯一商品ID
        const productIds = [...new Set(backendOrders.flatMap(order => order.items.map(item => item.id)))];

        // 并发获取所有商品详情
        const productRequests = productIds.map(id => axios.get(`http://algorineko.top:8080/api/merchant/product/detail/${id}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }).catch(error => {
          console.error(`获取商品${id}详情失败:`, error);
          return null;
        }));

        // 等待所有商品详情请求完成
        const productResponses = await Promise.all(productRequests);

        // 构建商品信息映射表
        const productMap = {};
        productResponses.forEach((res, index) => {
          if (res && res.data) {
            const product = res.data;
            productMap[productIds[index]] = {
              name: product.productName,
              // 使用第一张图片或默认图片
              image: product.images && product.images.length > 0 ? product.images[0] : 'https://via.placeholder.com/60'
            };
          }
        });

        // 更新订单中的商品信息
        backendOrders.forEach(order => {
          order.items.forEach(item => {
            const productInfo = productMap[item.id];
            if (productInfo) {
              item.name = productInfo.name;
              item.image = productInfo.image;
            }
          });
        });

        // 添加评价状态逻辑
        const reviews = JSON.parse(localStorage.getItem('productReviews') || '[]');
        backendOrders.forEach(order => {
          // 设置订单支付状态
          order.isPaid = ['PAID', 'SHIPPED', 'COMPLETED'].includes(order.status);

          // 设置每个商品项的评价状态
          order.items.forEach(item => {
            // 检查该商品是否已有评价
            const hasReview = reviews.some(review => review.orderItemId === item.orderItemId);
            item.reviewed = hasReview;
          });
        });

        // 保存到localStorage（保持原有功能）
        localStorage.setItem('orders', JSON.stringify(backendOrders));

        // 应用筛选条件（原有逻辑）
        let filteredOrders = [...backendOrders];

        // 状态筛选
        if (filterStatus.value !== 'all') {
          filteredOrders = filteredOrders.filter(order => order.status === filterStatus.value);
        }

        // 日期筛选
        if (filterDate.value && filterDate.value.length === 2) {
          const startDate = new Date(filterDate.value[0]);
          const endDate = new Date(filterDate.value[1]);
          endDate.setHours(23, 59, 59, 999); // 包含结束日期的全天

          filteredOrders = filteredOrders.filter(order => {
            const orderDate = new Date(order.createTime);
            return orderDate >= startDate && orderDate <= endDate;
          });
        }

        // 拆分订单为商品行
        const lines = [];
        filteredOrders.forEach(order => {
          order.items.forEach((item, index) => {
            lines.push({
              ...order,
              item,
              // 当前商品项
              isFirst: index === 0 // 标记是否是订单的第一行
            });
          });
        });

        // 分页处理
        const start = (currentPage.value - 1) * pageSize.value;
        orderLines.value = lines.slice(start, start + pageSize.value);
        total.value = lines.length;
      } catch (error) {
        console.error('订单加载失败:', error);

        // 更详细的错误信息
        let errorMessage = '订单加载失败';
        if (error.response) {
          // 服务器响应了错误状态码
          if (error.response.status === 401) {
            errorMessage = '用户未认证，请重新登录';
          } else if (error.response.status === 403) {
            errorMessage = '没有权限访问订单数据';
          } else if (error.response.data && error.response.data.message) {
            errorMessage = error.response.data.message;
          }
        } else if (error.request) {
          // 请求已发出但没有收到响应
          errorMessage = '无法连接到服务器，请检查网络连接';
        }
        ElMessage.error(errorMessage);
        orderLines.value = [];
      }
    };
    const openReviewDialog = orderLine => {
      currentReviewOrderLine.value = orderLine;
      const customerId = getCustomerIdFromToken();
      reviewForm.value = {
        orderItemId: orderLine.item.orderItemId,
        // 使用订单项ID
        customerId: customerId,
        // 设置用户ID
        rating: 5,
        content: ''
      };
      reviewDialogVisible.value = true;
    };
    const submitReview = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          ElMessage.error('用户未登录');
          return;
        }

        // 调用后端评价接口
        const response = await axios.post('http://algorineko.top:8080/api/customer/review/add', {
          orderItemId: reviewForm.value.orderItemId,
          customerId: reviewForm.value.customerId,
          rating: reviewForm.value.rating,
          comment: reviewForm.value.content
        }, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        if (response.data === '评论提交成功') {
          // 获取本地存储的评价记录
          const reviews = JSON.parse(localStorage.getItem('productReviews') || '[]');

          // 添加新的评价记录（使用订单项ID作为标识）
          reviews.push({
            orderItemId: reviewForm.value.orderItemId,
            rating: reviewForm.value.rating,
            content: reviewForm.value.content,
            date: new Date().toLocaleString(),
            itemSnapshot: {
              id: currentReviewOrderLine.value.item.id,
              name: currentReviewOrderLine.value.item.name,
              price: currentReviewOrderLine.value.item.price,
              quantity: currentReviewOrderLine.value.item.quantity,
              image: currentReviewOrderLine.value.item.image
            }
          });

          // 更新本地存储
          localStorage.setItem('productReviews', JSON.stringify(reviews));
          ElMessage.success('评价提交成功');
          reviewDialogVisible.value = false;

          // 重新加载订单数据更新评价状态
          loadOrders();
        } else {
          ElMessage.error('评价提交失败：' + response.data);
        }
      } catch (error) {
        console.error('评价提交失败:', error);
        let errorMessage = '评价提交失败';
        if (error.response) {
          if (error.response.data) {
            errorMessage += `: ${error.response.data}`;
          }
        }
        ElMessage.error(errorMessage);
      }
    };

    // 支付订单
    const payOrder = async orderId => {
      if (!orderId) {
        ElMessage.warning('无效的订单号');
        return;
      }

      // 调用API更新订单状态为已付款（使用大写状态字符串）
      const success = await updateOrderStatus(orderId, 'PAID');
      if (success) {
        // 更新本地存储
        const savedOrders = JSON.parse(localStorage.getItem('orders') || '[]');
        const updatedOrders = savedOrders.map(order => {
          if (order.orderId === orderId) {
            return {
              ...order,
              status: 'PAID',
              // 状态变为待发货
              isPaid: true
            };
          }
          return order;
        });
        localStorage.setItem('orders', JSON.stringify(updatedOrders));
        ElMessage.success(`订单 #${orderId} 支付成功，等待商家发货`);
        loadOrders();
      }
    };

    // 确认收货
    const confirmReceipt = async orderId => {
      if (!orderId) {
        ElMessage.warning('无效的订单号');
        return;
      }

      // 调用API更新订单状态为已完成（使用大写状态字符串）
      const success = await updateOrderStatus(orderId, 'COMPLETED');
      if (success) {
        // 更新本地存储
        const savedOrders = JSON.parse(localStorage.getItem('orders') || '[]');
        const updatedOrders = savedOrders.map(order => {
          if (order.orderId === orderId) {
            return {
              ...order,
              status: 'COMPLETED'
            };
          }
          return order;
        });
        localStorage.setItem('orders', JSON.stringify(updatedOrders));
        ElMessage.success(`订单 #${orderId} 确认收货成功`);
        loadOrders();
      }
    };

    // 删除订单
    const deleteOrder = orderId => {
      ElMessageBox.confirm('确定要删除这个订单吗？删除后无法恢复', '警告', {
        confirmButtonText: '确定删除',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        // 从本地存储中删除订单
        const savedOrders = JSON.parse(localStorage.getItem('orders') || '[]');
        const filteredOrders = savedOrders.filter(order => order.orderId !== orderId);
        localStorage.setItem('orders', JSON.stringify(filteredOrders));
        ElMessage.success('订单删除成功');
        loadOrders();
      }).catch(() => {
        // 用户取消了删除操作
      });
    };

    // 分页处理
    const handlePageChange = page => {
      currentPage.value = page;
      loadOrders();
    };

    // 显示物流详情
    const showTrackingInfo = async orderLine => {
      try {
        // 重置物流信息
        currentTrackingInfo.value = {
          logisticsCompany: '',
          trackingNumber: '',
          status: ''
        };

        // 调用物流接口
        const response = await axios.get(`http://algorineko.top:8080/api/order/tracking/${orderLine.orderId}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        // 获取当前订单项的物流信息
        const itemLogistics = response.data.itemLogistics;
        const orderItemId = orderLine.item.orderItemId;
        if (itemLogistics && itemLogistics[orderItemId]) {
          const logisticsInfo = itemLogistics[orderItemId];
          currentTrackingInfo.value = {
            logisticsCompany: logisticsInfo.logisticsCompany || '未知',
            trackingNumber: logisticsInfo.trackingNumber || '暂无',
            status: logisticsInfo.status || ''
          };
        } else {
          ElMessage.warning('未找到该订单项的物流信息');
        }
        trackingDialogVisible.value = true;
      } catch (error) {
        console.error('获取物流信息失败:', error);
        ElMessage.error('获取物流信息失败');
      }
    };

    // 初始化加载
    onMounted(() => {
      loadOrders();
    });
    const __returned__ = {
      statusText,
      statusType,
      parseJwt,
      statusOptions,
      orderLines,
      filterStatus,
      filterDate,
      total,
      pageSize,
      currentPage,
      reviewDialogVisible,
      reviewForm,
      currentReviewOrderLine,
      trackingDialogVisible,
      currentTrackingInfo,
      getCustomerIdFromToken,
      updateOrderStatus,
      loadOrders,
      openReviewDialog,
      submitReview,
      payOrder,
      confirmReceipt,
      deleteOrder,
      handlePageChange,
      showTrackingInfo,
      ref,
      onMounted,
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
