import axios from 'axios'

const apiClient = axios.create({
  baseURL: 'http://algorineko.top:8080',
  withCredentials: false,
  headers: {
    Accept: 'application/json',
    'Content-Type': 'application/json',
    // 添加以下CORS头
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Methods': 'GET,PUT,POST,DELETE,PATCH,OPTIONS'
  }
})

export default {
  async login(credentials) {
    try {
      const response = await apiClient.post('/user/login', {
        username: credentials.userId,
        password: credentials.password,
        userType: credentials.role  // 添加用户角色字段
      })
      
      return {
        data: {
          token: response.data.token || 'simulated-token',
          userId: credentials.userId,
          role: response.data.userType || ''  // 使用后端返回的角色
        }
      }
    } catch (error) {
      let errorMessage = '登录失败';
      if (error.response) {
        if (error.response.status === 401) {
          errorMessage = '用户ID或密码错误';
        } else if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        } else {
          errorMessage = `服务器错误: ${error.response.status}`;
        }
      } else if (error.request) {
        errorMessage = '无法连接到服务器，请检查网络';
      } else {
        errorMessage = error.message || '登录请求设置错误';
      }
      throw new Error(errorMessage);
    }
  },

  async register(user) {
    try {
      const response = await apiClient.post('/user/register', {
        username: user.userId,
        password: user.password,
        phone: user.phone || '',
        userType: user.role,  // 用户角色字段
        address: user.address || ''
      })
      
      return {
        data: {
          message: response.data.message || '注册成功'
        }
      }
    } catch (error) {
      let errorMessage = '注册失败';
      if (error.response) {
        if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        } else {
          errorMessage = `服务器错误: ${error.response.status}`;
        }
      } else if (error.request) {
        errorMessage = '无法连接到服务器，请检查网络';
      } else {
        errorMessage = error.message || '注册请求设置错误';
      }
      throw new Error(errorMessage);
    }
  }
}
