import { createRouter, createWebHistory } from 'vue-router'
import LoginView from '../views/LoginView.vue'

const routes = [
  {
    path: '/',
    redirect: '/login'
  },
  {
    path: '/login',
    name: 'Login',
    component: LoginView
  },
  {
    path: '/register',
    name: 'Register',
    component: () => import('../views/RegisterView.vue')
  },
  // 修改商家路由，添加用户名参数
  {
    path: '/business/:username',
    name: 'Business',
    component: () => import('../views/business/BusinessView.vue'),
    meta: { requiresAuth: true, role: 'merchant' },
    props: true, // 启用路由参数传递
    children: [
      {
        path: 'info',
        name: 'BusinessInfo',
        component: () => import('../views/business/BusinessInfo.vue')
      },
      {
        path: 'products',
        name: 'BusinessProducts',
        component: () => import('../views/business/ProductManagement.vue')
      },
      {
        path: 'orders',
        name: 'BusinessOrders',
        component: () => import('../views/business/OrderManagement.vue')
      },
      {
        path: 'reviews',
        name: 'BusinessReviews',
        component: () => import('../views/business/ReviewManagement.vue')
      },
      {
        path: '',
        redirect: { name: 'BusinessInfo' }
      }
    ]
  },
  // 修改买家路由，添加用户名参数
  {
    path: '/buyer/:username',
    name: 'Buyer',
    component: () => import('../views/Buyer/BuyerView.vue'),
    meta: { requiresAuth: true, role: 'customer' },
    props: true, // 启用路由参数传递
    children: [
      {
        path: '',
        redirect: '/buyer/:username/products'
      },
      {
        path: 'products',
        component: () => import('../views/Buyer/ProductList.vue')
      },
      {
        path: 'product/:id',
        component: () => import('../views/Buyer/ProductDetail.vue'),
        props: true
      },
      {
        path: 'user',
        component: () => import('../views/Buyer/UserCenter.vue'),
        children: [
          {
            path: '',
            redirect: '/buyer/:username/user/profile'
          },
          {
            path: 'profile',
            component: () => import('../components/UserProfile.vue')
          },
          {
            path: 'cart',
            component: () => import('../components/ShoppingCart.vue')
          },
          {
            path: 'orders',
            component: () => import('../components/UserOrders.vue')
          },
          {
            path: 'reviews',
            component: () => import('../components/UserReviews.vue')
          }
        ]
      }
    ]
  },
  {
    path: '/admin',
    name: 'Admin',
    component: () => import('../views/AdminView.vue'),
    meta: { requiresAuth: true, role: 'admin' }
  }
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

// 增强路由守卫
router.beforeEach((to, from, next) => {
  const isAuthenticated = localStorage.getItem('token')
  const userRole = localStorage.getItem('role')
  const username = localStorage.getItem('username') // 获取存储的用户名

  if (to.meta.requiresAuth) {
    if (!isAuthenticated) {
      next('/login')
    } else if (to.meta.role !== userRole) {
      next(from.path)
      alert('无权访问该页面')
    } else {
      // 验证路由中的用户名是否匹配
      if (to.params.username && to.params.username !== username) {
        // 重定向到当前用户的正确路径
        const routeName = to.name
        next({ name: routeName, params: { username } })
      } else {
        next()
      }
    }
  } else {
    next()
  }
})

export default router
